
function displayTableData(data){const tableBody=document.querySelector(".table-main");tableBody.innerHTML="";const dataArray=Object.values(data);dataArray.forEach(item=>{const row=createTableRow(item);tableBody.appendChild(row);});}
function createTableRow(item){const row=document.createElement("tr");row.classList.add("table-row");const customerNameCell=createTableCell(item["Customer Name"]);const companyCell=createTableCell(item["Company"]);const phoneNumberCell=createLinkTableCell(item["Phone Number"],`tel:${item["Phone Number"]}`);const emailCell=createLinkTableCell(item["Email"],`mailto:${item["Email"]}`);const countryCell=createTableCell(item["Country"]);const statusCell=createStatusTableCell(item["Status"]);row.appendChild(customerNameCell);row.appendChild(companyCell);row.appendChild(phoneNumberCell);row.appendChild(emailCell);row.appendChild(countryCell);row.appendChild(statusCell);return row;}
function createTableCell(text){const cell=document.createElement("td");cell.classList.add("td-text");cell.textContent=text;return cell;}
function createLinkTableCell(text,link){const cell=document.createElement("td");cell.classList.add("td-text");const linkElement=document.createElement("a");linkElement.href=link;linkElement.textContent=text;cell.appendChild(linkElement);return cell;}
function createStatusTableCell(status){const cell=document.createElement("td");cell.classList.add("td-text","td-status");const statusSpan=document.createElement("span");statusSpan.textContent=status;statusSpan.classList.add(status==="Active"?"td-active":"td-inactive");cell.appendChild(statusSpan);return cell;}
function fetchDataFromFirebase(){return fetch("https://dashboard-70ed8-default-rtdb.firebaseio.com/.json").then(response=>response.json()).then(data=>{return data;});}
fetchDataFromFirebase().then(data=>{displayTableData(data);});const searchForm=document.querySelector(".search-form");const searchInput=document.querySelector(".i-text");const tableBody=document.querySelector(".table-main");searchInput.addEventListener("input",event=>{const searchValue=event.target.value.toLowerCase();tableBody.innerHTML="";fetch("https://dashboard-70ed8-default-rtdb.firebaseio.com/.json").then(response=>response.json()).then(data=>{Object.values(data).forEach(item=>{const customerName=item["Customer Name"].toLowerCase();if(customerName.includes(searchValue)){const newRow=createTableRow(item);tableBody.appendChild(newRow);}});}).catch(error=>console.log(error));});